create table GTVENDORWEBSITE( VENDORWEBSITEID VARCHAR(45) NOT NULL, FK_VENDORID VARCHAR(45) NOT NULL,FK_USERID VARCHAR(45) NOT NULL, CREATEDATE BIGINT(20) NOT NULL DEFAULT 0, HUMANCREATEDATE VARCHAR(45), PRIMARY KEY (VENDORWEBSITEID) ) ENGINE = MyISAM DEFAULT CHARSET = utf8;
create table GTVENDORWEBSITEFEATURES ( VENDORWEBSITEFEATUREID VARCHAR(45) NOT NULL, FK_VENDORWEBSITEID  VARCHAR(45) NOT NULL, FEATURENAME  VARCHAR(75) NOT NULL, VALUE  TEXT NOT NULL,MODIFIEDDATE BIGINT(20) NOT NULL DEFAULT 0, HUMANMODIFIEDDATE VARCHAR(45),FK_USERID VARCHAR(45) NOT NULL, PRIMARY KEY (VENDORLANDINGPAGEFEATUREID) ) ENGINE = MyISAM  DEFAULT CHARSET = utf8;

